input_list = input["applyList"] || {}
groups = {}
output = []
debug = []

input_list.each do |i|
  invoice_customer = i["customer"]
  invoice_subsidiary = i["subsidiary"]
  invoice_currency = i["currency"]

  group_id = invoice_customer+"_"+invoice_subsidiary+"_"+invoice_currency
  debug << {"line" => "search for group "+group_id}

  if (!groups[group_id].present? )
    debug << {"line" => "group not found, creating it"}
  	# this group has not been created yet, create it now
    groups[group_id] = []
  else
    debug << {"line" => "group found"}
  end

  debug << {"line" => "adding apply item to "+group_id}
  groups[group_id] << i
end

debug << {"line" => "generating output"}

# add all groups to the output
groups.each do |key, value|
  debug << {"line" => "group "+key+" has "+value.length+" values"}

  output << {"applyList" => value}
end
debug << {"line" => "all items in group appended to output"}

{ payments: output, debug: debug }
